import React, { useEffect, useState } from "react";
import EmailEncrypt from "./Components/emailencrypt";
import Footer from "./Components/Footer";

const SocialLinks = () => {
  return (
    <div className="ContactLinks">
      {/* LinkedIn */}
      <a href="https://www.linkedin.com/in/notomer/" className="classic-tab" target="_blank" rel="noopener noreferrer" id="linkedin-link">
        <img src="/images/SetUpAssistant.png" alt="LinkedIn" className="icon rotated-image" />
        LinkedIn
        <div className="gloss-layer"></div>
      </a>

      {/* GitHub */}
      <a href="https://github.com/notomer" className="classic-tab" target="_blank" rel="noopener noreferrer" id="github-link">
        <img src="/images/dev.png" alt="GitHub" className="icon" />
        GitHub
        <div className="gloss-layer"></div>
      </a>

      {/* EmailEncrypt */}
      <EmailEncrypt className="classic-tab" target="_blank" rel="noopener noreferrer" id="email-link" />
    </div>
  );
};

const Contact = () => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkIfMobile();
    window.addEventListener("resize", checkIfMobile);

    return () => {
      window.removeEventListener("resize", checkIfMobile);
    };
  }, []);

  return (
    <div className={`App ${isMobile ? "mobile" : ""}`}>
      <div className="PageSwitcher">
        <a href="/" className="classic-tab">
          Portfolio
          <div className="gloss-layer"></div>
        </a>
        <a href="#/Contact" className="classic-tab active">
          Contact
          <div className="gloss-layer"></div>
        </a>
      </div>
      <div className="pfp">
        <img
          src={"./images/login.png"}
          alt="Me, Omer"
          style={{
            width: "10%",
            height: "10%",
            borderRadius: "50%",
            objectFit: "cover",
          }}
        />
      </div>
      <section className="Introduction">
        <h1>Hi, I'm Omer!</h1>
        <p>
          We either met, you were looking at my resume, or simply stumbled on
          this site. Either way, its a pleasure to meet you!
        </p>
        <p>
          Below I have included everywhere you can find/reach me and my resume,
          just tap (or click) the buttons. If you want to learn more about me
          click (or tap) the portfolio tab to learn more about me.
        </p>
      </section>
      <SocialLinks />
      <div className="contact-page-container">
        <Footer />
      </div>
    </div>
  );
};

export default Contact;
