import React, { useState, useEffect } from 'react';
import './styles.css'; // Make sure to import or create your CSS file

const GreetingsCarousel = () => {
  const greetings = [
    'Hello',
    'السلام عليكم',
    'नमस्ते',
    'ਸਤ ਸ੍ਰੀ ਅਕਾਲ',
    'Hola',
    'Բարեւ',
    'سلام',
    'สวัสดี',
    'こんにちは',
    '你好',
    'Привет',
    'Bonjour',
    'Ciao',
    'Merhaba',
    'Szia',
    'Hallo',
    'Olá',
    'Hej',
    '안녕하세요',
    'Γεια σας',
    'Ahoj',
    'Dia dhuit',
    'Salut',
    'Здравейте',
    'Halo',
    'שלום',
    'გამარჯობა',
    'Buna ziua',
    'Здравствуйте',
    'Jambo',
    'مرحبا',
    'Aloha',
    'Selamat pagi',
    'Xin chào',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const textChangeInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
    }, 8000); // Change the text every 8 seconds

    // Clear the interval when the component is unmounted
    return () => clearInterval(textChangeInterval);
  }, [greetings.length]);

  // Reset the interval when the greetings array changes
  useEffect(() => {
    clearInterval(textChangeInterval);
    const newInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
    }, 8000);
    return () => clearInterval(newInterval);
  }, [greetings]);

  return (
    <div className="fade-container">
      <p className="fade-text">{greetings[currentIndex]}</p>
    </div>
  );
};

export default GreetingsCarousel;
