import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { useGLTF, Stage, PresentationControls } from "@react-three/drei";

function Model(props) {
  const { scene } = useGLTF("/Porsche.glb");
  return <primitive object={scene} {...props} />;
}

function Porsche3d() {
  const carRef = useRef();
  const [rotationEnabled, setRotationEnabled] = useState(true);

  // Function to handle user interaction (click on the canvas)
  const handleInteraction = () => {
    setRotationEnabled(!rotationEnabled);
  };

  // useFrame hook to update rotation on each frame
  useFrame(() => {
    // Check if rotation is enabled
    if (rotationEnabled) {
      // Update rotation based on time or any other factor
      carRef.current.rotation.y += 0.01; // Adjust rotation speed as needed
    }
  });

  return (
    <Canvas
      dpr={[1, 2]}
      shadows
      camera={{ fov: 45 }}
      style={{ position: "absolute" }}
      onClick={handleInteraction}
    >
      <PresentationControls speed={1.5} global zoom={0.5} polar={[-0.1, Math.PI / 4]}>
        <Stage environment="">
          <Model
            scale={0.006}
            ref={carRef}
            rotation={[0, Math.PI / 3, 0]}
            onPointerOver={() => setRotationEnabled(false)}
            onPointerOut={() => setRotationEnabled(true)}
          />
        </Stage>
      </PresentationControls>
    </Canvas>
  );
}

export default Porsche3d;
