import React from 'react';

const EmailEncrypt = () => {
  const obfuscatedEmail = "npmfnpoljahnbjmAdpAfDq";

  // Decrypt the obfuscated email address
  const email = obfuscatedEmail
    .split('')
    .map(char => String.fromCharCode(char.charCodeAt(0) - 1))
    .join('');

  return (
    <span>
      <a
        href={`mailto:${email}`}
        onClick={(e) => {
          e.preventDefault();
          window.location.href = `mailto:${email}`;
        }}
      >
        <img src="/images/mail.png" alt="eMail" className="icon" />
        My Email
        <div className="gloss-layer"></div>
      </a>
    </span>
  );
};

export default EmailEncrypt;
