import React, { useState } from 'react';

const ProjectCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    { url: 'image1.jpg', description: 'Description for Image 1' },
    { url: 'image2.jpg', description: 'Description for Image 2' },
    { url: 'image3.jpg', description: 'Description for Image 3' },
    // Add more image URLs and descriptions as needed
  ];

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : images.length - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex < images.length - 1 ? prevIndex + 1 : 0));
  };

  return (
    <div>
      <button onClick={handlePrev}>&lt;</button>
      <div>
        <img src={images[currentIndex].url} alt={`Slide ${currentIndex + 1}`} />
        <p>{images[currentIndex].description}</p>
      </div>
      <button onClick={handleNext}>&gt;</button>
    </div>
  );
};

export default ProjectCarousel;
