import React, { useState, useEffect } from 'react';
import '../App.css'; 

const GreetingsCarousel = () => {
  const greetings = [
    'Hello',
    'السلام عليكم',
    'नमस्ते',
    'ਸਤ ਸ੍ਰੀ ਅਕਾਲ',
    'Hola',
    'Բարեւ',
    'สวัสดี',
    'こんにちは',
    '你好',
    'Привет',
    'Bonjour',
    'Ciao',
    'Merhaba',
    'Szia',
    'Hallo',
    'Olá',
    'Hej',
    '안녕하세요',
    'Γεια σας',
    'Ahoj',
    'Dia dhuit',
    'Salut',
    'Здравейте',
    'Halo',
    'გამარჯობა',
    'Buna ziua',
    'Здравствуйте',
    'Jambo',
    'مرحبا',
    'Aloha',
    'Selamat pagi',
    'Xin chào',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Show text for the first 8 seconds
    const textDuration = 8000;
    const animationDuration = 6000; // 6 seconds
    const totalDuration = textDuration + animationDuration;

    const startAnimation = setTimeout(() => {
      // Start the animation after 8 seconds
      const currentElement = document.querySelector('.fade-text');
      if (currentElement) {
        currentElement.style.animationDelay = '0s'; // No delay after starting
        currentElement.style.opacity = '1'; // Set opacity to 1 after starting
      }

      // Change the text every 6 seconds after the initial 8 seconds
      const intervalId = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
      }, animationDuration);

      // Clear the interval after the total duration
      setTimeout(() => {
        clearInterval(intervalId);
      }, totalDuration);
    }, textDuration);

    return () => clearTimeout(startAnimation);
  }, [greetings.length]);

  return (
    <div className="fade-container">
      <p className="fade-text">{greetings[currentIndex]}</p>
    </div>
  );
};

export default GreetingsCarousel;
