import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { useGLTF, Stage, PresentationControls } from "@react-three/drei";

// Component for the 3D model slideshow
const Slideshow = ({ models }) => {
  const [currentModelIndex, setCurrentModelIndex] = useState(0);

  const handleNext = () => {
    setCurrentModelIndex((prevIndex) => (prevIndex + 1) % models.length);
  };

  const handlePrev = () => {
    setCurrentModelIndex(
      (prevIndex) => (prevIndex - 1 + models.length) % models.length
    );
  };

  const { scene } = useGLTF(models[currentModelIndex].url);

  return (
    <div className="slideshow-container">
      <div className="arrow" onClick={handlePrev}>
        {'<'}
      </div>
      <Canvas>
        <Stage environment="" controls={OrbitControls}>
          <primitive object={scene} />
        </Stage>
      </Canvas>
      <div className="arrow" onClick={handleNext}>
        {'>'}
      </div>
      <div className="title-container">
        <h3>{models[currentModelIndex].title}</h3>
        {models[currentModelIndex].image && (
          <img
            src={models[currentModelIndex].image}
            alt={models[currentModelIndex].title}
          />
        )}
      </div>
    </div>
  );
};

// Main Porsche3d component
function Porsche3d() {
  const carRef = useRef();
  const [rotationEnabled, setRotationEnabled] = useState(true);
  const [rotation, setRotation] = useState([0, Math.PI / 4., 0]);

  let idleTimeout;

  const resetIdleTimer = () => {
    clearTimeout(idleTimeout);
    idleTimeout = setTimeout(() => {
      setRotationEnabled(true);
    }, 5000);
  };

  useEffect(() => {
    let requestId;
    let lastTimestamp = 0;

    const updateRotation = (timestamp) => {
      const elapsedMilliseconds = timestamp - lastTimestamp;

      if (rotationEnabled) {
        const rotationSpeed = 0.0001; // Adjust rotation speed as needed
        setRotation((prevRotation) => [
          prevRotation[0],
          prevRotation[1] + rotationSpeed * elapsedMilliseconds,
          prevRotation[2]
        ]);
      }

      lastTimestamp = timestamp;
      requestId = requestAnimationFrame(updateRotation);
    };

    if (rotationEnabled) {
      requestId = requestAnimationFrame(updateRotation);
    }

    // Initial idle timer setup
    resetIdleTimer();

    return () => {
      cancelAnimationFrame(requestId);
      clearTimeout(idleTimeout);
    };
  }, [rotationEnabled]);

  return (
    <div>
      <Slideshow models={models} />
      <Canvas
        dpr={[1, 2]}
        shadows
        camera={{ fov: 45 }}
        style={{ position: "absolute" }}
      >
        <ambientLight intensity={0.5} />
        <PresentationControls
          speed={1.5}
          global
          zoom={1}
          polar={[-0.1, Math.PI / 4.]}
          onPointerDown={() => {
            setRotationEnabled(true);
            resetIdleTimer();
          }}
          onPointerUp={() => {
            setRotationEnabled(false);
          }}
          onPointerLeave={() => {
            setRotationEnabled(false);
          }}
        >
          <Stage environment="">
            <Model scale={0.0006} ref={carRef} rotation={rotation} />
          </Stage>
        </PresentationControls>
      </Canvas>
    </div>
  );
}

export default Porsche3d;
