import React, { useRef, useEffect } from 'react';
import { USDZLoader } from 'three-usdz-loader';
import { useThree } from 'react-three-fiber';

const CarModel = ({ usdzUrl }) => {
  const modelRef = useRef();
  const { scene } = useThree();

  useEffect(() => {
    const loader = new USDZLoader();

    loader.load(usdzUrl, (usdzScene) => {
      scene.add(usdzScene);
      modelRef.current = usdzScene; // Assign the scene to the ref
    });

    return () => {
      // Clean up on component unmount
      if (modelRef.current) {
        scene.remove(modelRef.current);
      }
    };
  }, [usdzUrl, scene]);

  return null; // Returning null because we don't need to render anything specific for this component
};

export default CarModel;
