import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { useGLTF, Stage, PresentationControls } from "@react-three/drei";

function Model(props) {
  const { scene } = useGLTF("/Porsche.glb");
  return <primitive object={scene} {...props} />;
}

function Porsche3d() {
  const carRef = useRef();
  const [rotationEnabled, setRotationEnabled] = useState(true);
  const [rotation, setRotation] = useState([0, Math.PI / 4., 0]);

  const handleInteraction = () => {
    setRotationEnabled(!rotationEnabled);
  };

  useEffect(() => {
    let requestId;
    let lastTimestamp = 0;

    const updateRotation = (timestamp) => {
      const elapsedMilliseconds = timestamp - lastTimestamp;

      if (rotationEnabled) {
        const rotationSpeed = 0.001; // Adjust rotation speed as needed
        setRotation((prevRotation) => [
          prevRotation[0],
          prevRotation[1] + rotationSpeed * elapsedMilliseconds,
          prevRotation[2]
        ]);
      }

      lastTimestamp = timestamp;
      requestId = requestAnimationFrame(updateRotation);
    };

    if (rotationEnabled) {
      requestId = requestAnimationFrame(updateRotation);
    }

    return () => cancelAnimationFrame(requestId);
  }, [rotationEnabled]);

  return (
    <Canvas
      dpr={[1, 2]}
      shadows
      camera={{ fov: 45 }}
      style={{ position: "absolute" }}
      onClick={handleInteraction}
    >
      <PresentationControls speed={1.5} global zoom={0.5} polar={[-0.1, Math.PI / 4.]}>
        <Stage environment="">
          <Model
            scale={0.006}
            ref={carRef}
            rotation={rotation}
            onPointerOver={() => setRotationEnabled(false)}
            onPointerOut={() => setRotationEnabled(true)}
          />
        </Stage>
      </PresentationControls>
    </Canvas>
  );
}

export default Porsche3d;
