import React, { useState, useEffect } from 'react';
import '../App.css'; 

const GreetingsCarousel = () => {
  const greetings = [
    'Hello',
    'السلام عليكم',
    'नमस्ते',
    'ਸਤ ਸ੍ਰੀ ਅਕਾਲ',
    'Hola',
    'Բարեւ',
    'สวัสดี',
    'こんにちは',
    '你好',
    'Привет',
    'Bonjour',
    'Ciao',
    'Merhaba',
    'Szia',
    'Hallo',
    'Olá',
    'Hej',
    '안녕하세요',
    'Γεια σας',
    'Ahoj',
    'Dia dhuit',
    'Salut',
    'Здравейте',
    'Halo',
    'გამარჯობა',
    'Buna ziua',
    'Здравствуйте',
    'Jambo',
    'مرحبا',
    'Aloha',
    'Selamat pagi',
    'Xin chào',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const opacityDuration = 1000; // Set your desired opacity duration in milliseconds

  useEffect(() => {
    const textChangeInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
    }, 7000); // Change the text every 8 seconds

    // Clear the interval when the component is unmounted
    return () => clearInterval(textChangeInterval);
  }, [greetings.length]);

  const generateKeyframes = () => {
    const keyframes = `
      @keyframes fadeInOut {
        0%, 100% {
          opacity: 0;
        }
        ${(opacityDuration / 10000) * 100}% {
          opacity: 1;
        }
        100% {
          opacity: 0;
        }
      }
    `;

    return keyframes;
  };

  return (
    <>
      <style>{generateKeyframes()}</style>
      <div className="fade-container">
        <p className="fade-text">{greetings[currentIndex]}</p>
      </div>
    </>
  );
};

export default GreetingsCarousel;
