import React from 'react';

const EmailEncrypt = () => {
  const encodeEmail = (email) => {
    return email.split('').map(char => String.fromCharCode(char.charCodeAt(0) + 1)).join('');
  };

  const obfuscatedEmail = "notomerkhan@gmail.com";
  const decodedEmail = encodeEmail(obfuscatedEmail);

  return (
    <span>
      <a
        href={`mailto:${decodedEmail}`}
        onClick={(e) => {
          e.preventDefault();
          window.location.href = `mailto:${decodedEmail}`;
        }}
      >
        <img src="/images/mail.png" alt="eMail" className="icon" />
        My Email
        <div className="gloss-layer"></div>
      </a>
    </span>
  );
};

export default EmailEncrypt;
