import React, { useState, useEffect } from 'react';
import '../App.css'; 

const GreetingsCarousel = () => {
  const greetings = [
    'Hello',
    'السلام عليكم',
    'नमस्ते',
    'ਸਤ ਸ੍ਰੀ ਅਕਾਲ',
    'Hola',
    'Բարեւ',
    'สวัสดี',
    'こんにちは',
    '你好',
    'Привет',
    'Bonjour',
    'Ciao',
    'Merhaba',
    'Szia',
    'Hallo',
    'Olá',
    'Hej',
    '안녕하세요',
    'Γεια σας',
    'Ahoj',
    'Dia dhuit',
    'Salut',
    'Здравейте',
    'Halo',
    'გამარჯობა',
    'Buna ziua',
    'Здравствуйте',
    'Jambo',
    'مرحبا',
    'Aloha',
    'Selamat pagi',
    'Xin chào',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const startAnimation = setTimeout(() => {
      const currentElement = document.querySelector('.fade-text');
      if (currentElement) {
        const animation = currentElement.animate(
          [
            { opacity: 0 },
            { opacity: 1 },
            { opacity: 1, content: 'New Text' },
            { opacity: 0 },
            { opacity: 0 },
          ],
          {
            duration: 10000, // 3 seconds total duration
            iterations: Infinity,
          }
        );

        // Switch text at 2 seconds
        setTimeout(() => {
          currentElement.textContent = 'New Text';
        }, 10000); // 2 seconds (adjust as needed)
      }

      const intervalId = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
      }, 10000); // 3 seconds (adjust as needed)

      return () => clearInterval(intervalId);
    }, 8000); // Adjust the delay before starting the animation (in milliseconds)

    return () => clearTimeout(startAnimation);
  }, [greetings.length]);

  return (
    <div className="fade-container">
      <p className="fade-text">{greetings[currentIndex]}</p>
    </div>
  );
};

export default GreetingsCarousel;
