import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { useGLTF, Stage, PresentationControls } from "@react-three/drei";

function Model(props) {
  const { scene } = useGLTF("/Porsche.glb");
  return <primitive object={scene} {...props} />;
}

function Porsche3d() {
  const carRef = useRef();
  const [rotationEnabled, setRotationEnabled] = useState(true);

  const handleInteraction = () => {
    setRotationEnabled(!rotationEnabled);
  };

  useEffect(() => {
    let timeoutId;

    const handleIdleTimeout = () => {
      setRotationEnabled(true);
    };

    const handlePointerMove = () => {
      // Reset the idle timeout on user interaction
      clearTimeout(timeoutId);
      timeoutId = setTimeout(handleIdleTimeout, 5000); // Adjust the idle time (in milliseconds) as needed
    };

    window.addEventListener("mousemove", handlePointerMove);

    // Set initial idle timeout
    timeoutId = setTimeout(handleIdleTimeout, 5000); // Adjust the idle time (in milliseconds) as needed

    return () => {
      window.removeEventListener("mousemove", handlePointerMove);
      clearTimeout(timeoutId);
    };
  }, []); // Run this effect only once on component mount

  return (
    <Canvas
      dpr={[1, 2]}
      shadows
      camera={{ fov: 45 }}
      style={{ position: "absolute" }}
      onClick={handleInteraction}
    >
      <PresentationControls speed={1.5} global zoom={0.5} polar={[-0.1, Math.PI / 4.]}>
        <Stage environment="">
          <Model
            scale={0.006}
            ref={carRef}
            rotation={[0, Math.PI / 4., 0]}
            onPointerOver={() => setRotationEnabled(false)}
            onPointerOut={() => setRotationEnabled(true)}
            rotation={[0, rotationEnabled ? Math.PI / 4. : 0, 0]} // Update rotation based on rotationEnabled
          />
        </Stage>
      </PresentationControls>
    </Canvas>
  );
}

export default Porsche3d;
