import React, { useEffect, useState } from 'react';
import './Links.css';
import EmailEncrypt from './Components/emailencrypt';
import Porsche3d from './Components/Porsche3d';

const Links = () => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkIfMobile();
    window.addEventListener('resize', checkIfMobile);

    return () => {
      window.removeEventListener('resize', checkIfMobile);
    };
  }, []);

  return (
    <div className={`App ${isMobile ? 'mobile' : ''}`}>

      <div className='PageSwitcher'>
        <a href="/">
          Portfolio
        </a>   
        <a href="Links">
          Links 
        </a>
      </div>
      <div className="pfp">
        <img
          src={"./images/login.png"}
          alt="Me, Omer"
          style={{ width: '10%', height: '10%', borderRadius: '50%', objectFit: 'cover' }}
        />
      </div>
      <section className="Introduction">
        <h1>Hi, I'm Omer!</h1>
        <h2>We either met, you were looking at my resume, or simply stumbled on this site. Either way, its a pleasure to meet you!</h2>
        <p>Below I have included everywhere you can find/reach me and my resume. If you want to learn more about me click the portfolio tab to learn more about me</p>
      </section>
      <section id="footer" className="section">
        <div className="madeby">
          <h2>
            Researched, designed, and engineered by me,{' '}
            <span style={{ color: 'black' }}>Omer</span>.
          </h2>
        </div>
        <div className="copyright">
          <h2>&copy; {new Date().getFullYear()} | <span style={{ color: 'black' }}>notomer</span></h2>
        </div>
      </section>
    </div>
  );
};

export default Links;
