import React, { useState, useEffect } from 'react';
import '../App.css'; 

const GreetingsCarousel = () => {
  const greetings = [
    'Hello',
    'السلام عليكم',
    'नमस्ते',
    'ਸਤ ਸ੍ਰੀ ਅਕਾਲ',
    'Hola',
    'Բարեւ',
    'สวัสดี',
    'こんにちは',
    '你好',
    'Привет',
    'Bonjour',
    'Ciao',
    'Merhaba',
    'Szia',
    'Hallo',
    'Olá',
    'Hej',
    '안녕하세요',
    'Γεια σας',
    'Ahoj',
    'Dia dhuit',
    'Salut',
    'Здравейте',
    'Halo',
    'გამარჯობა',
    'Buna ziua',
    'Здравствуйте',
    'Jambo',
    'مرحبا',
    'Aloha',
    'Selamat pagi',
    'Xin chào',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const startAnimation = setTimeout(() => {
      const currentElement = document.querySelector('.fade-text');
      if (currentElement) {
        currentElement.style.animationDelay = '0s'; // No delay after starting
        currentElement.style.opacity = '1'; // Set opacity to 1 after starting
      }

      const intervalId = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % greetings.length);
      }, 9000); // Change the interval duration as needed (in milliseconds)

      return () => clearInterval(intervalId);
    }, 8700); // Start the animation after 6 seconds (adjust as needed)

    return () => clearTimeout(startAnimation);
  }, [greetings.length]);

  return (
    <div className="fade-container">
      <p className="fade-text">{greetings[currentIndex]}</p>
    </div>
  );
};

export default GreetingsCarousel;
