import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { useGLTF, Stage, PresentationControls } from '@react-three/drei';

function Model(props) {
  const { scene } = useGLTF("/Porsche.glb");
  return <primitive object={scene} {...props} />;
}

function Porsche3d() {
  const modelRef = useRef();

  useFrame(() => {
    // Your animation logic here
    if (modelRef.current) {
      modelRef.current.rotation.y += 0.005; // Rotate the model around the y-axis
    }
  });

  return (
    <Canvas dpr={[1, 2]} shadows={false} camera={{ fov: 45 }} style={{ position: 'absolute' }}>
      <color attach="background" args={['#101010']} />
      <PresentationControls speed={1.5} global zoom={0.5} polar={[-0.1, Math.PI / 4]}>
        <Stage environment="sunset">
          <Model ref={modelRef} scale={0.006} />
        </Stage>
      </PresentationControls>
    </Canvas>
  );
}

export default Porsche3d;
